func (suite *StateDBTestSuite) TestGetHashFn() {
	testCase := []struct {
		name         string
		height       uint64
		malleate     func()
		expEmptyHash bool
	}{
		{
			"valid hash, case 1",
			1,
			func() {
				suite.ctx.SetBlockHeader(
					abci.Header{
						ChainID:        "ethermint-1",
						Height:         1,
						ValidatorsHash: []byte("val_hash"),
					},
				)
				hash := ethcmn.BytesToHash([]byte("test hash"))
				suite.stateDB.SetBlockHash(hash)
			},
			false,
		},
		{
			"case 1, nil tendermint hash",
			1,
			func() {},
			true,
		},
		{
			"height not found, case 2",
			1,
			func() {
				suite.ctx.SetBlockHeader(
					abci.Header{
						ChainID:        "ethermint-1",
						Height:         100,
						ValidatorsHash: []byte("val_hash"),
					},
				)
			},
			true,
		},
		{
			"valid hash, case 2",
			1,
			func() {
				suite.ctx.SetBlockHeader(
					abci.Header{
						ChainID:        "ethermint-1",
						Height:         100,
						ValidatorsHash: []byte("val_hash"),
					},
				)
				hash := ethcmn.BytesToHash([]byte("test hash"))
				suite.stateDB.WithContext(suite.ctx).SetHeightHash(1, hash)
			},
			false,
		},
		{
			"empty hash, case 3",
			1000,
			func() {
				suite.ctx.SetBlockHeader(
					abci.Header{
						ChainID:        "ethermint-1",
						Height:         100,
						ValidatorsHash: []byte("val_hash"),
					},
				)
			},
			true,
		},
	}

	for _, tc := range testCase {
		suite.Run(tc.name, func() {
			suite.SetupTest() // reset

			tc.malleate()

			hash := types.GetHashFn(suite.ctx, suite.stateDB)(tc.height)
			if tc.expEmptyHash {
				suite.Require().Equal(common.Hash{}.String(), hash.String(), tc.name)
			} else {
				suite.Require().NotEqual(common.Hash{}.String(), hash.String(), tc.name)
			}
		})
	}
}

func (suite *StateDBTestSuite) TestTransitionDb() {
	suite.stateDB.SetNonce(suite.address, 123)

	addr := sdk.AccAddress(suite.address.Bytes())
	balance := ethermint.NewPhotonCoin(sdk.NewInt(5000))
	acc := suite.app.AccountKeeper.GetAccount(suite.ctx, addr)
	_ = acc.SetCoins(sdk.NewCoins(balance))
	suite.app.AccountKeeper.SetAccount(suite.ctx, acc)

	suite.stateDB = types.CreateEmptyCommitStateDB(suite.app.EvmKeeper.GenerateCSDBParams(), suite.ctx)

	priv, err := ethsecp256k1.GenerateKey()
	suite.Require().NoError(err)
	recipient := ethcrypto.PubkeyToAddress(priv.ToECDSA().PublicKey)

	testCase := []struct {
		name     string
		malleate func()
		state    types.StateTransition
		expPass  bool
	}{
		{
			"passing state transition",
			func() {},
			types.StateTransition{
				AccountNonce: 123,
				Price:        sdk.NewDec(10).BigInt(),
				GasLimit:     11,
				Recipient:    &recipient,
				Amount:       sdk.NewDec(50).BigInt(),
				Payload:      []byte("data"),
				ChainID:      big.NewInt(1),
				Csdb:         suite.stateDB,
				TxHash:       &ethcmn.Hash{},
				Sender:       suite.address,
				Simulate:     suite.ctx.IsCheckTx(),
			},
			true,
		},
		{
			"contract creation",
			func() {},
			types.StateTransition{
				AccountNonce: 123,
				Price:        sdk.NewDec(10).BigInt(),
				GasLimit:     11,
				Recipient:    nil,
				Amount:       sdk.NewDec(10).BigInt(),
				Payload:      []byte("data"),
				ChainID:      big.NewInt(1),
				Csdb:         suite.stateDB,
				TxHash:       &ethcmn.Hash{},
				Sender:       suite.address,
				Simulate:     suite.ctx.IsCheckTx(),
			},
			true,
		},
		{
			"fail by sending more than balance",
			func() {},
			types.StateTransition{
				AccountNonce: 123,
				Price:        sdk.NewDec(10).BigInt(),
				GasLimit:     11,
				Recipient:    &recipient,
				Amount:       sdk.NewDec(500000).BigInt(),
				Payload:      []byte("data"),
				ChainID:      big.NewInt(1),
				Csdb:         suite.stateDB,
				TxHash:       &ethcmn.Hash{},
				Sender:       suite.address,
				Simulate:     suite.ctx.IsCheckTx(),
			},
			false,
		},
		{
			"call disabled",
			func() {
				params := types.NewParams(true, false, false, false, maxGasLimitPerTx)
				suite.stateDB.SetParams(params)
			},
			types.StateTransition{
				AccountNonce: 123,
				Price:        big.NewInt(10),
				GasLimit:     11,
				Recipient:    &recipient,
				Amount:       big.NewInt(50),
				Payload:      []byte("data"),
				ChainID:      big.NewInt(1),
				Csdb:         suite.stateDB,
				TxHash:       &ethcmn.Hash{},
				Sender:       suite.address,
				Simulate:     suite.ctx.IsCheckTx(),
			},
			false,
		},
		{
			"create disabled",
			func() {
				params := types.NewParams(false, true, false, false, maxGasLimitPerTx)
				suite.stateDB.SetParams(params)
			},
			types.StateTransition{
				AccountNonce: 123,
				Price:        big.NewInt(10),
				GasLimit:     11,
				Recipient:    nil,
				Amount:       big.NewInt(50),
				Payload:      []byte("data"),
				ChainID:      big.NewInt(1),
				Csdb:         suite.stateDB,
				TxHash:       &ethcmn.Hash{},
				Sender:       suite.address,
				Simulate:     suite.ctx.IsCheckTx(),
			},
			false,
		},
		{
			"nil gas price",
			func() {
				suite.stateDB.SetParams(types.DefaultParams())
				invalidGas := sdk.DecCoins{
					{Denom: ethermint.NativeToken},
				}
				suite.ctx.SetMinGasPrices(invalidGas)
			},
			types.StateTransition{
				AccountNonce: 123,
				Price:        sdk.NewDec(10).BigInt(),
				GasLimit:     11,
				Recipient:    &recipient,
				Amount:       sdk.NewDec(10).BigInt(),
				Payload:      []byte("data"),
				ChainID:      big.NewInt(1),
				Csdb:         suite.stateDB,
				TxHash:       &ethcmn.Hash{},
				Sender:       suite.address,
				Simulate:     suite.ctx.IsCheckTx(),
			},
			true, // it is expected to succeed because `EnableCall` is set to `true` by default.
		},
		{
			"state transition simulation",
			func() {
				params := types.NewParams(false, true, false, false, maxGasLimitPerTx)
				suite.stateDB.SetParams(params)
			},
			types.StateTransition{
				AccountNonce: 123,
				Price:        sdk.NewDec(10).BigInt(),
				GasLimit:     11,
				Recipient:    &recipient,
				Amount:       sdk.NewDec(10).BigInt(),
				Payload:      []byte("data"),
				ChainID:      big.NewInt(1),
				Csdb:         suite.stateDB,
				TxHash:       &ethcmn.Hash{},
				Sender:       suite.address,
				Simulate:     true,
			},
			true,
		},
		{
			"contract failed call addr is blocked",
			func() {
				params := types.NewParams(false, true, false, true, maxGasLimitPerTx)
				suite.stateDB.SetParams(params)

				suite.stateDB.SetCode(common.BytesToAddress(callAcc.Bytes()), callBuffer)
				suite.stateDB.SetCode(common.BytesToAddress(blockedAcc.Bytes()), blockedBuffer)
				blockedList := types.AddressList{blockedAcc}
				suite.stateDB.SetContractBlockedList(blockedList)
			},
			types.StateTransition{
				AccountNonce: 123,
				Price:        sdk.NewDec(10).BigInt(),
				GasLimit:     100000000,
				Recipient:    &blockedEthAcc,
				Amount:       sdk.NewDec(0).BigInt(),
				Payload:      hexutil.MustDecode("0x371303c0"),
				ChainID:      big.NewInt(1),
				Csdb:         suite.stateDB,
				TxHash:       &ethcmn.Hash{},
				Sender:       suite.address,
				Simulate:     suite.ctx.IsCheckTx(),
			},
			false,
		},
		{
			"contract failed call contract method blocked",
			func() {
				params := types.NewParams(false, true, false, true, maxGasLimitPerTx)
				suite.stateDB.SetParams(params)
				suite.stateDB.SetCode(common.BytesToAddress(callAcc.Bytes()), callBuffer)
				suite.stateDB.SetCode(common.BytesToAddress(blockedAcc.Bytes()), blockedBuffer)
				suite.stateDB.SetContractMethodBlocked(blockedContract)
			},
			types.StateTransition{
				AccountNonce: 123,
				Price:        sdk.NewDec(10).BigInt(),
				GasLimit:     100000000,
				Recipient:    &callEthAcc,
				Amount:       sdk.NewDec(0).BigInt(),
				Payload:      callMethodBlocked,
				ChainID:      big.NewInt(1),
				Csdb:         suite.stateDB,
				TxHash:       &ethcmn.Hash{},
				Sender:       suite.address,
				Simulate:     suite.ctx.IsCheckTx(),
			},
			false,
		},
		{
			"contract failed callcode contract method blocked",
			func() {
				params := types.NewParams(false, true, false, true, maxGasLimitPerTx)
				suite.stateDB.SetParams(params)
				suite.stateDB.SetCode(common.BytesToAddress(callAcc.Bytes()), callBuffer)
				suite.stateDB.SetCode(common.BytesToAddress(blockedAcc.Bytes()), blockedBuffer)
				suite.stateDB.SetContractMethodBlocked(blockedContract)
			},
			types.StateTransition{
				AccountNonce: 123,
				Price:        sdk.NewDec(10).BigInt(),
				GasLimit:     100000000,
				Recipient:    &callEthAcc,
				Amount:       sdk.NewDec(0).BigInt(),
				Payload:      callcodeMethodBlocked,
				ChainID:      big.NewInt(1),
				Csdb:         suite.stateDB,
				TxHash:       &ethcmn.Hash{},
				Sender:       suite.address,
				Simulate:     suite.ctx.IsCheckTx(),
			},
			false,
		},
		{
			"contract failed delegate call contract method blocked",
			func() {
				params := types.NewParams(false, true, false, true, maxGasLimitPerTx)
				suite.stateDB.SetParams(params)
				suite.stateDB.SetCode(common.BytesToAddress(callAcc.Bytes()), callBuffer)
				suite.stateDB.SetCode(common.BytesToAddress(blockedAcc.Bytes()), blockedBuffer)
				suite.stateDB.SetContractMethodBlocked(blockedContract)
			},
			types.StateTransition{
				AccountNonce: 123,
				Price:        sdk.NewDec(10).BigInt(),
				GasLimit:     100000000,
				Recipient:    &callEthAcc,
				Amount:       sdk.NewDec(0).BigInt(),
				Payload:      delegatecallMethodBlocked,
				ChainID:      big.NewInt(1),
				Csdb:         suite.stateDB,
				TxHash:       &ethcmn.Hash{},
				Sender:       suite.address,
				Simulate:     suite.ctx.IsCheckTx(),
			},
			false,
		},
		{
			"contract failed selfdestruct contract method blocked",
			func() {
				params := types.NewParams(false, true, false, true, maxGasLimitPerTx)
				suite.stateDB.SetParams(params)

				suite.stateDB.CreateAccount(callEthAcc)
				suite.stateDB.CreateAccount(blockedEthAcc)
				suite.stateDB.SetCode(callEthAcc, callBuffer)
				suite.stateDB.SetCode(blockedEthAcc, blockedBuffer)

				suite.stateDB.SetContractMethodBlocked(blockedContract)
			},
			types.StateTransition{
				AccountNonce: 123,
				Price:        sdk.NewDec(10).BigInt(),
				GasLimit:     100000000,
				Recipient:    &callEthAcc,
				Amount:       sdk.NewDec(0).BigInt(),
				Payload:      selfdestructMethodBlocked,
				ChainID:      big.NewInt(1),
				Csdb:         suite.stateDB,
				TxHash:       &ethcmn.Hash{},
				Sender:       suite.address,
				Simulate:     suite.ctx.IsCheckTx(),
			},
			false,
		},
	}

	for _, tc := range testCase {
		tc.malleate()

		_, _, err, _, _ = tc.state.TransitionDb(suite.ctx, types.DefaultChainConfig())

		if tc.expPass {
			suite.Require().NoError(err, tc.name)
		} else {
			suite.Require().Error(err, tc.name)
		}
	}
	fromBalance := suite.app.EvmKeeper.GetBalance(suite.ctx, suite.address)
	toBalance := suite.app.EvmKeeper.GetBalance(suite.ctx, recipient)
	suite.Require().Equal(sdk.NewDec(4930).BigInt(), fromBalance)
	suite.Require().Equal(sdk.NewDec(60).BigInt(), toBalance)
}
